import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Reservation.css"; // Import the CSS file

const formatDate = (dateString) => {
  return new Date(dateString).toISOString().split('T')[0];
};

function Reservation({ reservation, query }) {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [mail, setMail] = useState("");
  const [goDate, setGoDate] = useState(formatDate(query.go));
  const [returnDate, setReturnDate] = useState(formatDate(query.ret));

  const navigate = useNavigate();

  const handleAdd = async () => {
    query.name = name;
    query.mail = mail;
    query.phone = phone;
    try {
      await axios.post("http://localhost:3000/api/flight/reservation/post", query);
    } catch (e) {
      console.error(e);
    }
  };

  return (
    <div className="container">
      <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input type="text" placeholder="Mail" value={mail} onChange={(e) => setMail(e.target.value)} />
      <input type="text" placeholder="Phone Number" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <div>
        <input type="text" placeholder="Type" value={query.type} readOnly />
        <span className="required-asterisk">*</span>
      </div>
      <div>
        <input type="text" placeholder="ID Type" value={query.id_type} readOnly />
        <span className="required-asterisk">*</span>
      </div>
      <div>
        <input type="text" placeholder="Capacity" value={query.capacite} readOnly />
        <span className="required-asterisk">*</span>
      </div>

      {query.id_type === 'Flight' ? (
        <>
          <div>
            <input type="text" placeholder="Go Date" value={formatDate(query.go)} readOnly />
            <span className="required-asterisk">*</span>
          </div>
          <div>
            <input type="text" placeholder="Return Date" value={formatDate(query.ret)} readOnly />
            <span className="required-asterisk">*</span>
          </div>
          <div>
            <input type="text" placeholder="Destination" value={query.resName} readOnly />
            <span className="required-asterisk">*</span>
          </div>
        </>
      ) : (
        <>
          <input type="date" placeholder="Go Date" value={goDate} onChange={(e) => setGoDate(e.target.value)} />
          <input type="date" placeholder="Return Date" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
        </>
      )}

      <div>
        <input type="text" placeholder="Price" value={query.price} readOnly />
        <span className="required-asterisk">*</span>
      </div>
      <button onClick={handleAdd}>Add</button>
      {query.id_type === 'Flight' ? (
        <button className="cancel" onClick={() => navigate('/flights')}>Cancel</button>
      ) : (
        <button className="cancel" onClick={() => navigate("/hotel")}>Cancel</button>
      )}
      <div className="disclaimer">
        * The fields with the * need to be filled manually.
      </div>
    </div>
  );
}

export default Reservation;
