import React, { useState } from "react";
import axios from "axios";
import AddedRes from "./AddedRes.jsx";
import { useNavigate } from "react-router-dom";
import "./Flight.css"; // Import the CSS file

function Flight({ flights, setQuery }) {
  const navigate = useNavigate();
  const [editing, setEditing] = useState(null);
  const [capacite, setCapacite] = useState("");
  const [price, setPrice] = useState("");
  const [type, setType] = useState("");
  const [destination, setDestination] = useState("");
  const [go, setGo] = useState("");
  const [ret, setRet] = useState("");

  const handleEdit = (id) => {
    setEditing(id);
    const flight = flights.find((e) => e.id === id);
    if (flight) {
      setCapacite(flight.capacite);
      setType(flight.type_flight);
      setPrice(flight.price);
      setDestination(flight.destination);
      setGo(flight.go_date);
      setRet(flight.return_date);
    }
  };

  const handelDel = (id) => {
    try {
      axios.delete(`http://localhost:3000/api/flight/delete/${id}`);
    } catch (e) {
      console.log(e);
    }
  };

  const updt = async (id, query) => {
    try {
      await axios.put(`http://localhost:3000/api/flight/update/${id}`, query);
    } catch (e) {
      console.error(e);
    }
    setEditing(null);
  };

  const handleUpdate = (id) => {
    const query = {
      type_flight: type,
      capacite: capacite,
      go_date: go,
      return_date: ret,
      destination: destination,
      price: price,
    };
    updt(id, query);
  };

  const handleRes = (flight) => {
    setQuery({
      name: '',
      phone: '',
      mail: '',
      id: flight.id,
      id_type: "Flight",
      type: flight.type_flight,
      capacite: flight.capacite,
      go: flight.go_date,
      ret: flight.return_date,
      resName: flight.destination,
      price: flight.price,
      etoile: null,
      description: null,
    });
    navigate("/reservation")
  }

  const formatDate = (dateString) => {
    return new Date(dateString).toISOString().split('T')[0];
  };

  return (
    <>
      <button id="add-flight-button" onClick={() => navigate("/flight/add")}>Add a flight</button>
      {flights.map((flight) => (
        <div key={flight.id} className="flight-card">
          <ul className="flight-details">
            {editing === flight.id ? (
              <>
                <input
                  id="edit-type"
                  type="text"
                  placeholder="Type"
                  value={type}
                  onChange={(e) => setType(e.target.value)}
                />
                <input
                  id="edit-capacite"
                  type="text"
                  placeholder="Capacity"
                  value={capacite}
                  onChange={(e) => setCapacite(e.target.value)}
                />
                <input
                  id="edit-go"
                  type="text"
                  placeholder="Go Date"
                  value={go}
                  onChange={(e) => setGo(e.target.value)}
                />
                <input
                  id="edit-return"
                  type="text"
                  placeholder="Return Date"
                  value={ret}
                  onChange={(e) => setRet(e.target.value)}
                />
                <input
                  id="edit-destination"
                  type="text"
                  placeholder="Destination"
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                />
                <input
                  id="edit-price"
                  type="text"
                  placeholder="Price"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                />
              </>
            ) : (
              <>
                <li id="view-type">Type: {flight.type_flight}</li>
                <li id="view-capacite">Capacity: {flight.capacite}</li>
                <li id="view-go">Go Date: {formatDate(flight.go_date)}</li>
                <li id="view-return">Return Date: {formatDate(flight.return_date)}</li>
                <li id="view-destination">Destination: {flight.destination}</li>
                <li id="view-price">Price: {flight.price}</li>
              </>
            )}
          </ul>
          {editing === flight.id ? (
            <button id="confirm-button" onClick={() => handleUpdate(flight.id)}>Confirm</button>
          ) : (
            <button id="update-button" onClick={() => handleEdit(flight.id)}>Update</button>
          )}
          {editing === flight.id ? (
            <button id="cancel-button" onClick={() => setEditing(null)}>Cancel</button>
          ) : (
            <button id="delete-button" onClick={() => handelDel(flight.id)}>Delete</button>
          )}
          {editing !== flight.id && (
            <button id="reserve-button" onClick={() => handleRes(flight)}>Reservation</button>
          )}
        </div>
      ))}
    </>
  );
}

export default Flight;
