import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

function Hotel({setQuery }) {
    const [edit, setEdit] = useState(null);
    const [capacite, setCapacite] = useState('');
    const [price, setPrice] = useState('');
    const [type, setType] = useState('');
    const [name, setName] = useState('');
    const [etoile, setEtoile] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState('');
    const [hotels, setHotels] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchHotels();
    }, [hotels]);

    const fetchHotels = async () => {
        try {
            const response = await axios.get('http://localhost:3000/api/hotels/select'); 
            setHotels(response.data);
        } catch (error) {
            console.error('Error fetching hotels:', error);
        }
    };

    const handleEdit = (id) => {
        setEdit(id);
        const hotel = hotels.find((e) => e._id === id);
        if (hotel) {
            setCapacite(hotel.capacite);
            setType(hotel.type);
            setPrice(hotel.price);
            setName(hotel.name);
            setEtoile(hotel.etoile);
            setDescription(hotel.description);
            setImage(hotel.image);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3000/api/hotels/hotel/delete/${id}`);
        } catch (error) {
            console.error(error);
        }
    };

    const handleUpdate = async (id) => {
        const query = {
            price: price,
            type: type,
            capacite: capacite,
            name: name,
            etoile: etoile,
            description: description,
            image: image
        };
        try {
            await axios.put(`http://localhost:3000/api/hotels/hotel/update/${id}`, query);
        } catch (error) {
            console.error(error);
        }
        setEdit(null);
    };

    const handleRes=(hotl)=>{
      setQuery({
        name:'',
        phone:'',
        mail:'',
        id: hotl._id,
        id_type: "Hotel reservation",
        type: hotl.type,
        capacite: hotl.capacite,
        rating: hotl.etoile,
        hotName: hotl.hotl,
        description:hotl.description,
        price: hotl.price,
      });
      navigate("/reservation")
    }

    return (
        <React.Fragment>
          <button onClick={() => navigate("/hotels/add")}>Add a hotel</button>
            {hotels.map((hotel) => (
                <div key={hotel._id}>
                    <ul>
                        {edit === hotel._id ? (
                            <>
                                <input type="text" placeholder="Type" value={type} onChange={(e) => setType(e.target.value)} />
                                <input type="text" placeholder="Capacity" value={capacite} onChange={(e) => setCapacite(e.target.value)} />
                                <input type="text" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} />
                                <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
                                <input type="text" placeholder="Star" value={etoile} onChange={(e) => setEtoile(e.target.value)} />
                                <input type="text" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
                                <input type="text" placeholder="Image URL" value={image} onChange={(e) => setImage(e.target.value)} />
                            </>
                        ) : (
                            <>
                                <li>Type: {hotel.type}</li>
                                <li>Capacity: {hotel.capacite}</li>
                                <li>Price: {hotel.price}</li>
                                <li>Name: {hotel.name}</li>
                                <li>Star: {hotel.etoile}</li>
                                <li>Description: {hotel.description}</li>
                                <li>Image: {hotel.image}</li>
                            </>
                        )}
                    </ul>
                    {edit === hotel._id ? (
                        <button onClick={() => handleUpdate(hotel._id)}>Confirm</button>
                    ) : (
                        <button onClick={() => handleEdit(hotel._id)}>Update</button>
                    )}
                    <button onClick={() => handleDelete(hotel._id)}>Delete</button>
                    {edit === hotel._id && (
                        <button onClick={() => setEdit(null)}>Cancel</button>
                    )}
                </div>
            ))}
        </React.Fragment>
    );
}

export default Hotel;
